  // Arama fonksiyonu (scroll yapmadan)
  const findCategory = (query: string): string | null => {
    if (!query.trim()) return null;

    const normalizedQuery = query.toLowerCase().trim();
    
    // 1. √ñnce tam e≈üle≈ümeleri kontrol et (en y√ºksek √∂ncelik)
    const exactMatch = packageCategories.find(category => {
      const categoryName = category.name.toLowerCase();
      const categoryId = category.id.toLowerCase();
      
      // Tam e≈üle≈üme kontrol√º
      if (categoryName === normalizedQuery || categoryId === normalizedQuery) {
        return true;
      }
      
      // Kelime bazlƒ± tam e≈üle≈üme (√∂rn: "turkey" === "turkey")
      const categoryWords = categoryName.split(/\s+/);
      if (categoryWords.some(word => word === normalizedQuery)) {
        return true;
      }
      
      return false;
    });
    
    if (exactMatch) return exactMatch.id;
    
    // 2. Alias kontrol√º - Query bir alias mƒ±?
    for (const [key, aliases] of Object.entries(countryAliases)) {
      // Query tam olarak bir alias mƒ±?
      if (aliases.includes(normalizedQuery)) {
        // Bu alias'ƒ±n hangi kategoriye ait olduƒüunu bul
        const category = packageCategories.find(cat => {
          const catName = cat.name.toLowerCase();
          const catId = cat.id.toLowerCase();
          return catName === key || catId === key || catName.includes(key) || catId.includes(key);
        });
        if (category) return category.id;
      }
    }
    
    // 3. Kategori ismi query'yi i√ßeriyor mu? (en az 3 karakter, kesin e≈üle≈üme)
    if (normalizedQuery.length >= 3) {
      const categoryNameMatch = packageCategories.find(category => {
        const categoryName = category.name.toLowerCase();
        const categoryId = category.id.toLowerCase();
        
        // Kategori ismi query'yi ba≈ütan i√ßeriyor mu? (daha kesin)
        if (categoryName.startsWith(normalizedQuery) || categoryId.startsWith(normalizedQuery)) {
          return true;
        }
        
        // Veya query kategori ismini i√ßeriyor mu? (sadece uzun query'ler i√ßin)
        if (normalizedQuery.length >= 4 && (categoryName.includes(normalizedQuery) || categoryId.includes(normalizedQuery))) {
          return true;
        }
        
        return false;
      });
      
      if (categoryNameMatch) return categoryNameMatch.id;
    }

    return null;
  };

